<script>
	import { reactive } from 'vue'
	
	export default {
		name: 'UpdateRowSettingsComponent',
		props: reactive({
					draggable: {},
					mode: "add"
				}),
		data: () => ({
				rowIndex: 1,
		}),
		methods: {
			decreaseRowIndex() {
				this.rowIndex--
			},
			increaseRowIndex() {
				this.rowIndex++
			},
			getRowIndex(type) {
				if(type == 'low') {
					if(this.rowIndex == 1) {
						return this.rowIndex
					}
					
					return this.rowIndex - 1
				}
				else if(type == 'high') {
					if(this.rowIndex == this.draggable.rows - 1) {
						return this.rowIndex
					}
					
					return this.rowIndex + 1
				}
			}
		}
	}
</script>